import React, { useState } from "react";

const ContactForm = () => {
  // useState hook to manage state in functional component
  // formState is the state object that holds the form data
  // setFormState is a function used to update the formState object
  const [formState, setFormState] = useState({
    name: "",
    email: "",
    phone: "",
    message: "",
  });

  // handleChange updates the formState object with the new input value
  // when the input value changes
  const handleChange = (event) => {
    // event.target is the input element that triggered the event
    // destructuring the name and value properties from event.target
    const { name, value } = event.target;
    // updating the formState object using the spread operator and computed property names
    setFormState({ ...formState, [name]: value });
  };

  // handleSubmit is a function that is called when the form is submitted
  const handleSubmit = (event) => {
    // preventing the default refresh of the page on form submission
    event.preventDefault();
    // submit the form
  };

  // rendering the JSX for the form
  return (
    <div className="contact-form">
      <nav className="side-navbar">
        <h1>Get in touch</h1>
        <p>
          We'd love to hear from you. Our friendly team is always here to chat.
        </p>
        <br></br>
        <h2>Chat with us</h2>
        <p>Our friendly team is here to help.</p>
        <br></br>
        <h2>Office</h2>
        <p>Come say hello at our office HQ Tom Mboya street, Nyayo Plaza</p>
        <br></br>
        <h2>Phone</h2>
        <p>Mon-Fri from 8am to 5pm. +25478998654</p>
      </nav>
      <div className="welcome-message">
        <h1>Welcome!</h1>
      </div>
      {/* form element with onSubmit event handler that calls handleSubmit */}
      <form onSubmit={handleSubmit}>
        {/* label and input elements for name */}
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          name="name"
          value={formState.name}
          onChange={handleChange}
          placeholder="Enter your name"
        />
        {/* label and input elements for email */}
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          name="email"
          value={formState.email}
          onChange={handleChange}
          placeholder="Enter your email"
        />
        {/* label and input elements for phone */}
        <label htmlFor="phone">Phone:</label>
        <input
          type="tel"
          name="phone"
          value={formState.phone}
          onChange={handleChange}
          placeholder="Enter your phone number"
        />
        {/* label and textarea elements for message */}
        <label htmlFor="message">How can we help?</label>
        <textarea
          name="message"
          value={formState.message}
          onChange={handleChange}
          placeholder="Enter your message"
        />
        {/* button to submit the form */}
        <button type="submit">Get started</button>
      </form>
    </div>
  );
};

export default ContactForm;
